mapper.select:
SELECT COUNT(*) FROM Board
WHERE


		OR title LIKE #{keyword}



		OR content LIKE #{keyword}



		OR writer LIKE #{keyword}

SELECT 
	id,
	title,
	writer,
	inserted
FROM Board
WHERE



		OR title LIKE #{keyword}



		OR content LIKE #{keyword}



		OR writer LIKE #{keyword}



ORDER BY id DESC
LIMIT #{offset}, #{records}
